import { Container, Navbar, Nav} from "react-bootstrap";
import { Link } from "react-router-dom";

export default function Header(){
    onst [user, dispatch] = useContext(UserContext)

    const logout = (evt) => {
        evt.preventDefault()

        dispatch({
            "type": "logout"
        })
    }

    let btn = <>
        <Link to="/login" className="nav-link text-danger">Đăng nhập</Link>
        <Link to="/register" className="nav-link text-danger">Đăng ký</Link>
    </>
    if (user != null)
        btn = <>
            <Link to="/" className="nav-link text-danger">{user.username}</Link>
            <a href="#" onClick={logout} className="nav-link text-danger">Đăng xuất</a>
        </>

    return(
        <Navbar bg="light" expand="lg">
            <Container>
                <Navbar.Brand href="/">HỆ THỐNG GIỚI THIỆU VIỆC LÀM</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="me-auto">
                    <Link className="nav-link" to="/">Home</Link>
                    <Link className="nav-link" to="/gioi-thieu">Giới thiệu</Link>
                    <Link className="nav-link" to="/employer">Nhà tuyển dụng</Link>
                    <Link className="nav-link" to="/login">Dang nhap</Link>
                </Nav>
                </Navbar.Collapse>
            </Container>
            </Navbar>
    )
    
}
