import { useState } from "react"


const Login = () => {
    const [username, setUsername] = useStatee()
    const [password, setPassword] = useState()
    const [user, dispatch] = useContext(UserContext)

    const login = async (evt) => {
        evt.preventDefault()

        const res = await Apis.post(endpoints['login'], {
            'username': username,
            'password': password,
            'client_id': 'DEIuIsmYwmXYGPdqU1x0FtL0MF7MlcFPguGRYZx5',
            'client_secret': 'UCHEu8jfr2qPnw0dd8Pz6oJyymWaa0RnRF6Dos3vhzz0mNEbJ6AjIYjKWJpTWunpGOkVTDos06DroU6joAyf7Bn1pPEFY8Fnjb20VotdhotS0SRXagDPpfPUrszpm2BY',
            'grant_type': 'password'
        })

        console.info(res.data)
        cookie.save('token', res.data.access_token)

        const user = await authApi().get(endpoints['current-user'])
        console.info(user.data)
        
        dispatch({
            'type': 'login',
            'payload': user.data
        })
    }

    if (user != null)
        return <Navigate to="/" />

    return (
        <>
        <Container>
            <h1 className="text-center text-danger">ĐĂNG NHẬP</h1>
            <Form onSubmit={login}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="text" 
                        value={username} 
                        onChange={(evt) => setUsername(evt.target.value)}
                        placeholder="Nhập username" />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" 
                            value={password} 
                            onChange={(evt) => setPassword(evt.target.value)}
                            placeholder="Nhập password" />
                </Form.Group>

                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>
        </Container>
        </>
    )
}

export default Login
