import { useEffect, useState } from "react";
import { Button, Col, Image, Row, Spinner } from "react-bootstrap";
import { useParams } from "react-router-dom";
import Api, { endpoints } from "../configs/Api";

const WorkDetail = () => {
    const [work, setWorks] = useState([])
    const { workId } = useParams()

    useEffect(() => {
        let res;
        const loadWorkById = async () => {
            res = await Api.get(endpoints['work-detail'](workId))
            setWorks(res.data)
        }
        loadWorkById()
    }, []) 

    const ungTuyen = async (evt) => {
        evt.preventDefault()

        const res = await Api.post(endpoints['login'], {
            'applycandidate': username,
            'password': password,
            'client_id': 'DAzNOlbzrdBbIaY4uAEIHtPAtKECwQ6IJ8R858Jt',
            'client_secret': 'uFxe5UbfKctH6RfSlKa6EY3LYGSTD6ji71KIWVqywBV5RW7OtCFdZqpBotZElLkPYAaa2gK0pYDSWOO0Xk1jqeZUeq2PuDsFdcUgPoOHb3enWWpYaKjjJnW85cWbQigH',
            'grant_type': 'password'
        })
    }

    if(work===null)
        return <Spinner animation="border"/>
    return (
        <>
        <h1 class="text-center text-danger">Chi tiết công việc</h1>
        
        <Row>
            <Col md={5} xs={12}>   
                 <Image src={work.image} rounded fluid />
            </Col>
            <Col md = {4} xs={12}>
                <h3>{work.title}</h3>
                <p>Vị trí tuyển dụng: {work.vacancies}</p>
                <p>Địa chỉ: {work.address}</p>
                <p>Mức lương: {work.salary}</p>
                <p>Kinh nghiệm: {work.experience}</p>
                <p>Trình độ học vấn: {work.education}</p>
                <p>Mô tả: {work.describe}</p>
                <Button variant="primary" type="submit">
                    Ứng tuyển
                </Button>
            </Col>
        </Row>
        </>
    )
}
export default WorkDetail;
