import axios from "axios"


export let endpoints ={
    "work" : "/work/",
    "work-detail" : (workId) => `/work/${workId}/`,
    "employer" : "/employer/",
    "employer-detail" : (employerId) => `/work/${employerId}/`
}

export default axios.create({
    baseURL : "http://127.0.0.1:8000/"
})
