import { useEffect, useState } from "react";
import { Col, Image, Row, Spinner } from "react-bootstrap";
import { useParams } from "react-router-dom";
import Api, { endpoints } from "../configs/Api";

const WorkDetail = () => {
    const [work, setWorks] = useState(null)
    let { workId} = useParams

    useEffect(() => {
        const loadWorkById = async () => {
            try{
                let res = await Api.get((endpoints['work-detail'](workId)));
                setWorks(res.data.results)
            } catch(err){   
                console.error(err)
            } 
        }
        WorkDetail
    }, [])

    return (
        <>
        <h1 class="text-center text-danger">Chi tiết công việc</h1>
        <Row>
            <Col md={5} xs={12}>   
                 <Image src={work.image} fluid />
            </Col>
            <Col md = {4} xs={12}>
                <h3>work.title</h3>
                <p>
                </p>
            </Col>
        </Row>
        </>
    )
}
export default WorkDetail;
